import React, { useCallback, useState } from "react";
import styled from "styled-components";

const St = {
    Container: styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgb(231, 234, 239);
  `,
    Login: styled.div`
    width: 30%;
    height: 600px;
    background: white;
    border-radius: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  `,
    Head: styled.div`
    margin-bottom: 1rem;
    color: white;
    font-size: 3em;
    font-weight: bolder;
    text-shadow: 1px 2px 3px black;
  `,
    Login_sns: styled.div`
    padding: 20px;
    display: flex;
  `,
    Login_id: styled.div`
    margin-top: 20px;
    width: 80%;
  `,
    Input: styled.input`
    width: 100%;
    height: 50px;
    border-radius: 30px;
    margin-top: 10px;
    padding: 0px 20px;
    border: 1px solid lightgray;
    outline: none;
  `,
    Login_pw: styled.div`
    margin-top: 20px;
    width: 80%;
  `,
    Join_name: styled.div`
    margin-top: 20px;
    width: 80%;
`,
    Submit: styled.div`
    margin-top: 50px;
    width: 80%;
   `,
    Submit_input: styled.input`
    width: 100%;
    height: 50px;
    border: 0;
    outline: none;
    border-radius: 40px;
    background: linear-gradient(to right, rgb(231, 234, 239), #f7b5c9);
    color: white;
    font-size: 1.2em;
    letter-spacing: 2px;
    cursor:pointer;
    &:hover {
        background: linear-gradient(to right, #f7b5c9, #f7b5c9);
        color: white;
    }
    `,

    ToJoin: styled.a`
    margin: auto;
    text-decoration-line: none;
    text-decoration-line: none;
    `,

};

const JoinForm = () => {
  //이메일, 이름, 비밀번호, 비밀번호 확인
  const [email, setEmail] = useState<string>('')
  const [name, setName] = useState<string>('')
  const [password, setPassword] = useState<string>('')
  const [passwordConfirm, setPasswordConfirm] = useState<string>('')

  //오류메세지 상태 저장
  const [emailMessage, setEmailMessage] = useState<string>('');
  const [nameMessage, setNameMessage] = useState<string>('');
  const [passwordMessage, setPasswordMessage] = useState<string>('');
  const [passwordConfirmMessage, setPasswordConfirmMessage] = useState<string>('');

  //유효성 검사
  const [isEmail, setIsEmail] = useState<Boolean>(false)
  const [isName, setIsNams] = useState<Boolean>(false)
  const [isPassword, setIsPassword] = useState<Boolean>(false)
  const [isPasswordConfirm, setIsPasswordConfirm] = useState<Boolean>(false)
  const router = useRouter();

  return (
    <St.Container>
        <St.Login>
            <St.Head>WELLCOME</St.Head>
            <St.Login_id>
                <h4>E-mail</h4>
                <St.Input type="email" name="" id="" placeholder="Email" />
            </St.Login_id>
            <St.Join_name>
                <h4>Name</h4>
                <St.Input type="text" name="" id="" placeholder="Name" />
            </St.Join_name>
            <St.Login_pw>
                <h4>Password</h4>
                <St.Input type="password" name="" id="" placeholder="Password" />
            </St.Login_pw>
            <St.Login_pw>
                <h4>Password Confirm</h4>
                <St.Input type="password" name="" id="" placeholder="Password Confirm" />
            </St.Login_pw>
            <St.Submit>
                <St.Submit_input type="submit" value="submit"></St.Submit_input>
            </St.Submit>
        </St.Login>
    </St.Container>
  )
}

export default JoinForm;
